CREATE MEMORY TABLE "PUBLIC"."BOARD" COMMENT '게시물 테이블' (
    "BOARD_ID" BIGINT GENERATED BY DEFAULT AS IDENTITY(START WITH 1 RESTART WITH 6) DEFAULT ON NULL COMMENT '게시물 식별자' NOT NULL,
    "BOARD_TITLE" CHARACTER VARYING(200) COMMENT '게시물 제목' NOT NULL,
    "BOARD_DELETE_YN" BOOLEAN DEFAULT 0 COMMENT '게시물 삭제여부' NOT NULL,
    "CREATE_DATE" TIMESTAMP(6) DEFAULT CURRENT_TIMESTAMP COMMENT '게시물 생성일' NOT NULL,
    "UPDATE_DATE" TIMESTAMP(6) DEFAULT CURRENT_TIMESTAMP COMMENT '게시물 최종수정일' NOT NULL
);
ALTER TABLE "PUBLIC"."BOARD" ADD CONSTRAINT "PUBLIC"."CONSTRAINT_3" PRIMARY KEY("BOARD_ID");


